package com.tonyzhai.blog.domain;import com.sun.istack.internal.NotNull;import javax.persistence.Embedded;import javax.persistence.Entity;import javax.persistence.Id;/** * Article domain model * * @author Tony Zhai * @since 13-2-24 AM1:36 */@Entitypublic class Article {    @Id    @NotNull    private int id;    @Embedded    private BaseInfo baseInfo;    @NotNull    private String title;    @NotNull    private String content;    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public BaseInfo getBaseInfo() {        return baseInfo;    }    public void setBaseInfo(BaseInfo baseInfo) {        this.baseInfo = baseInfo;    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public String getContent() {        return content;    }    public void setContent(String content) {        this.content = content;    }}