package com.tonyzhai.blog.domain;import javax.persistence.Embeddable;import javax.persistence.Temporal;import javax.persistence.TemporalType;import java.util.Date;/** * Base Info * * @author Tony Zhai * @since 13-2-24 AM1:39 */@Embeddablepublic class BaseInfo {    private String createdName;    private int createdId;    @Temporal(TemporalType.TIMESTAMP)    private Date createdDate;    private String modifiedName;    private int modifiedId;    @Temporal(TemporalType.TIMESTAMP)    private Date modifiedDate;    public String getCreatedName() {        return createdName;    }    public void setCreatedName(String createdName) {        this.createdName = createdName;    }    public int getCreatedId() {        return createdId;    }    public void setCreatedId(int createdId) {        this.createdId = createdId;    }    public Date getCreatedDate() {        return createdDate;    }    public void setCreatedDate(Date createdDate) {        this.createdDate = createdDate;    }    public String getModifiedName() {        return modifiedName;    }    public void setModifiedName(String modifiedName) {        this.modifiedName = modifiedName;    }    public int getModifiedId() {        return modifiedId;    }    public void setModifiedId(int modifiedId) {        this.modifiedId = modifiedId;    }    public Date getModifiedDate() {        return modifiedDate;    }    public void setModifiedDate(Date modifiedDate) {        this.modifiedDate = modifiedDate;    }}