package com.tonyzhai.blog.domain.base.domain.support;import com.tonyzhai.blog.domain.base.exception.BaseException;import com.tonyzhai.blog.domain.base.exception.ExceptionCode;import org.codehaus.jackson.JsonParser;import org.codehaus.jackson.map.DeserializationContext;import org.codehaus.jackson.map.JsonDeserializer;import java.io.IOException;import java.text.ParseException;import java.text.SimpleDateFormat;import java.util.Date;/** * Json date deserializer * * @author Tony Zhai * @since 13-2-26 AM3:42 */public class JsonDateDeserializer extends JsonDeserializer<Date> {    @Override    public Date deserialize(JsonParser jsonparser, DeserializationContext deserializationcontext) throws IOException {        try {            return new SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss").parse(jsonparser.getText());        } catch(ParseException e) {            throw new BaseException(ExceptionCode.CONVERT_DATE_ERROR.getCode(), e.getMessage());        }    }}