package com.tonyzhai.blog.domain.security.domain;import com.tonyzhai.blog.domain.base.domain.BaseInfo;/** * User domain model * * @author Tony Zhai * @since 13-2-24 PM5:02 */public class User {    private String id;    private String firstName;    private String lastName;    private String username;    private String password;    private BaseInfo baseInfo = new BaseInfo();    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getFirstName() {        return firstName;    }    public void setFirstName(String firstName) {        this.firstName = firstName;    }    public String getLastName() {        return lastName;    }    public void setLastName(String lastName) {        this.lastName = lastName;    }    public String getUsername() {        return username;    }    public void setUsername(String username) {        this.username = username;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }    public BaseInfo getBaseInfo() {        return baseInfo;    }    public void setBaseInfo(BaseInfo baseInfo) {        this.baseInfo = baseInfo;    }    public String getFullName() {        return this.firstName + " " + this.lastName;    }}