package com.tonyzhai.shop.domain.base.support;import java.util.ArrayList;import java.util.List;/** * Data page * * @author Tony Zhai * @since 13-2-24 PM10:01 */public class DataPage<T> {    private final static int DEFAULT_PAGE_SIZE = 10;    private int pageSize = DEFAULT_PAGE_SIZE;    private int start;    private List<T> data;    private long totalCount;    public DataPage() {        this(new ArrayList<T>(),0, DEFAULT_PAGE_SIZE,0);    }    public DataPage(List<T> data,int start,int pageSize,long totalSize) {        this.pageSize = pageSize;        this.start = start;        this.totalCount = totalSize;        this.data = data;    }    public long getTotalCount() {        return this.totalCount;    }    public int getTotalPageCount() {        return DataPage.getTotalPageCount(this.totalCount, this.pageSize);    }    public int getPageSize() {        return pageSize;    }    public List<T> getData() {        return data;    }    public int getCurrentPageNo() {        return start / pageSize + 1;    }    public boolean hasNextPage() {        return this.getCurrentPageNo() < this.getTotalPageCount();    }    public boolean hasPreviousPage() {        return this.getCurrentPageNo() > 1;    }    protected static int getStartOfPage(int pageNo) {        return getStartOfPage(pageNo, DEFAULT_PAGE_SIZE);    }    public static int getStartOfPage(int pageNo, int pageSize) {        return (pageNo - 1) * pageSize;    }    public void setData(List<T> data) {        this.data = data;    }    public int getStart() {        return start;    }    public void setStart(int start) {        this.start = start;    }    public void setPageSize(int pageSize) {        this.pageSize = pageSize;    }    public void setTotalCount(int totalCount) {        this.totalCount = totalCount;    }    @Override    public String toString() {        return "DataPage [pageSize=" + pageSize + ", start=" + start + ", data=" + data + ", totalCount=" + totalCount + "]";    }    public static int getTotalPageCount(long totalCount, int pageSize) {        if(totalCount % pageSize == 0)            return Integer.valueOf(String.valueOf(totalCount / pageSize));        else            return Integer.valueOf(String.valueOf(totalCount / pageSize + 1));    }}