package com.tonyzhai.shop.domain.base.support;import org.codehaus.jackson.JsonGenerator;import org.codehaus.jackson.JsonProcessingException;import org.codehaus.jackson.map.JsonSerializer;import org.codehaus.jackson.map.SerializerProvider;import java.io.IOException;import java.text.SimpleDateFormat;import java.util.Date;/** * Json date serializer * * @author Tony Zhai * @since 13-2-26 AM3:42 */public class JsonDateSerializer extends JsonSerializer<Date> {    @Override    public void serialize(Date date, JsonGenerator jsonGenerator, SerializerProvider serializerProvider) throws IOException, JsonProcessingException {        jsonGenerator.writeString(new SimpleDateFormat("yyyy-MM-dd").format(date));    }}