package com.tonyzhai.shop.domain.base.support.exception;/** * Add some description * * @author Tony Zhai * @since 13-2-24 PM10:15 */public class BaseException extends RuntimeException{    public final static long	serialVersionUID	= 12345678;    private boolean				debug				= true;    private String				trace;    private int					code				= ExceptionCode.SYSTEM_ERROR.getCode();    public BaseException() {        super();        init();    }    public BaseException(String message) {        super(message);        init();    }    public BaseException(int code, String message) {        super(message);        this.code = code;        init();    }    public BaseException(String message, Throwable throwable) {        super(message, throwable);        init();    }    public BaseException(int code, String message, Throwable throwable) {        super(message, throwable);        this.code = code;        init();    }    public BaseException(Throwable throwable) {        super(throwable);        init();    }    public int getCode() {        return this.code;    }    public String getMessage() {        return (debug ? trace : "") + super.getMessage();    }    private void init() {        StackTraceElement traces[] = getStackTrace();        String className = traces[0].getClassName();        int n = className.lastIndexOf('.');        if(n > 0)            className = className.substring(n + 1);        trace = className + "." + traces[0].getMethodName() + "[line: " + traces[0].getLineNumber() + "]: ";    }}