package com.tonyzhai.shop.domain.portal.goods;import com.tonyzhai.shop.domain.base.BaseInfo;import org.codehaus.jackson.annotate.JsonIgnoreProperties;import org.hibernate.annotations.GenericGenerator;import javax.persistence.*;/** * Brand domain model * * @author Tony Zhai * @since 13-5-3 PM10:45 */@GenericGenerator(name = "brand", strategy = "uuid")@Entity@JsonIgnoreProperties({        "version"})public class Brand {    @Id    @GeneratedValue(generator = "brand")    private String id;    @Version    private Integer version;    @Embedded    private BaseInfo baseInfo = new BaseInfo();    private String name;    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public Integer getVersion() {        return version;    }    public void setVersion(Integer version) {        this.version = version;    }    public BaseInfo getBaseInfo() {        return baseInfo;    }    public void setBaseInfo(BaseInfo baseInfo) {        this.baseInfo = baseInfo;    }}