package com.tonyzhai.shop.domain.portal.goods;import com.tonyzhai.shop.domain.base.BaseInfo;import org.codehaus.jackson.annotate.JsonIgnoreProperties;import org.hibernate.annotations.GenericGenerator;import javax.persistence.*;import java.math.BigDecimal;import java.util.List;/** * Goods domain model * * @author Tony Zhai * @since 13-5-3 PM9:36 */@GenericGenerator(name = "goods", strategy = "uuid")@Entity@JsonIgnoreProperties({        "version"})public class Goods {    @Id    @GeneratedValue(generator = "goods")    private String id;    @Version    private Integer version;    @Embedded    private BaseInfo baseInfo = new BaseInfo();    private String name;    private String brand;    private String size;    private String expiredDate;    private String producePlace;    private BigDecimal marketPrice;    private BigDecimal webPrice;    private String smallImagePath;    private String bigImagePath;    @Transient    private int buyCount;    private String sn;    private int level;    @Lob    private String details;    @OneToMany    @JoinColumn(name="goods_id")    private List<GoodsComment> goodsCommentList;    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public Integer getVersion() {        return version;    }    public void setVersion(Integer version) {        this.version = version;    }    public BaseInfo getBaseInfo() {        return baseInfo;    }    public void setBaseInfo(BaseInfo baseInfo) {        this.baseInfo = baseInfo;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getBrand() {        return brand;    }    public void setBrand(String brand) {        this.brand = brand;    }    public String getSize() {        return size;    }    public void setSize(String size) {        this.size = size;    }    public String getExpiredDate() {        return expiredDate;    }    public void setExpiredDate(String expiredDate) {        this.expiredDate = expiredDate;    }    public String getProducePlace() {        return producePlace;    }    public void setProducePlace(String producePlace) {        this.producePlace = producePlace;    }    public BigDecimal getMarketPrice() {        return marketPrice;    }    public void setMarketPrice(BigDecimal marketPrice) {        this.marketPrice = marketPrice;    }    public BigDecimal getWebPrice() {        return webPrice;    }    public void setWebPrice(BigDecimal webPrice) {        this.webPrice = webPrice;    }    public String getSmallImagePath() {        return smallImagePath;    }    public void setSmallImagePath(String smallImagePath) {        this.smallImagePath = smallImagePath;    }    public String getBigImagePath() {        return bigImagePath;    }    public void setBigImagePath(String bigImagePath) {        this.bigImagePath = bigImagePath;    }    public int getBuyCount() {        return buyCount;    }    public void setBuyCount(int buyCount) {        this.buyCount = buyCount;    }    public String getSn() {        return sn;    }    public void setSn(String sn) {        this.sn = sn;    }    public int getLevel() {        return level;    }    public void setLevel(int level) {        this.level = level;    }    public String getDetails() {        return details;    }    public void setDetails(String details) {        this.details = details;    }    public List<GoodsComment> getGoodsCommentList() {        return goodsCommentList;    }    public void setGoodsCommentList(List<GoodsComment> goodsCommentList) {        this.goodsCommentList = goodsCommentList;    }}