package com.tonyzhai.shop.domain.portal.goods;import com.tonyzhai.shop.domain.base.BaseInfo;import com.tonyzhai.shop.domain.portal.goods.support.GoodsCommentCategory;import org.codehaus.jackson.annotate.JsonIgnoreProperties;import org.hibernate.annotations.GenericGenerator;import javax.persistence.*;/** * Goods comment domain model * * @author Tony Zhai * @since 13-5-3 PM9:55 */@GenericGenerator(name = "goodsComment", strategy = "uuid")@Entity@JsonIgnoreProperties({        "version"})public class GoodsComment {    @Id    @GeneratedValue(generator = "goodsComment")    private String id;    @Version    private Integer version;    @Embedded    private BaseInfo baseInfo = new BaseInfo();    private String title;    @Lob    private String content;    private GoodsCommentCategory goodsCommentCategory;    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public Integer getVersion() {        return version;    }    public void setVersion(Integer version) {        this.version = version;    }    public BaseInfo getBaseInfo() {        return baseInfo;    }    public void setBaseInfo(BaseInfo baseInfo) {        this.baseInfo = baseInfo;    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public String getContent() {        return content;    }    public void setContent(String content) {        this.content = content;    }    public GoodsCommentCategory getGoodsCommentCategory() {        return goodsCommentCategory;    }    public void setGoodsCommentCategory(GoodsCommentCategory goodsCommentCategory) {        this.goodsCommentCategory = goodsCommentCategory;    }}