package com.tonyzhai.shop.domain.security;import com.tonyzhai.shop.domain.base.BaseInfo;import org.codehaus.jackson.annotate.JsonIgnoreProperties;import org.hibernate.annotations.GenericGenerator;import org.springframework.util.StringUtils;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.Id;import javax.persistence.Version;/** * User domain model * * @author Tony Zhai * @since 13-2-24 PM5:02 */@Entity@GenericGenerator(name = "user_uuid", strategy = "uuid")@JsonIgnoreProperties({        "version",        "password"})public class User {    @Id    @GeneratedValue(generator = "user_uuid")    private String id;    @Version    private Integer version;    private String firstName;    private String lastName;    private String Username;    private String password;    private BaseInfo baseInfo = new BaseInfo();    public User() {    }    public User(String Username, String password) {        this.Username = Username;        this.password = password;    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public Integer getVersion() {        return version;    }    public void setVersion(Integer version) {        this.version = version;    }    public String getFirstName() {        return firstName;    }    public void setFirstName(String firstName) {        this.firstName = firstName;    }    public String getLastName() {        return lastName;    }    public void setLastName(String lastName) {        this.lastName = lastName;    }    public String getUsername() {        return Username;    }    public void setUsername(String Username) {        this.Username = Username;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }    public BaseInfo getBaseInfo() {        return baseInfo;    }    public void setBaseInfo(BaseInfo baseInfo) {        this.baseInfo = baseInfo;    }    public String getFullName() {        return (StringUtils.hasText(this.firstName) && StringUtils.hasText(this.lastName)) ? this.firstName + " " + this.lastName : null;    }}