package com.tonyzhai.shop.persistence.impl;import com.tonyzhai.shop.domain.Article;import com.tonyzhai.shop.domain.base.BaseInfo;import com.tonyzhai.shop.domain.base.support.DataPage;import com.tonyzhai.shop.domain.base.support.column.BaseInfoColumn;import com.tonyzhai.shop.persistence.ArticleDao;import com.tonyzhai.shop.persistence.base.impl.BaseDaoImpl;import org.hibernate.Criteria;import org.hibernate.criterion.Restrictions;/** * Article persistence layer impl * * @author Tony Zhai * @since 13-2-24 PM5:16 */public class ArticleDaoImpl extends BaseDaoImpl<Article> implements ArticleDao {    @Override    public void updateStatus(String id, BaseInfo.Status status) {        Article article = findById(id);        article.getBaseInfo().setStatus(status);        update(article);    }    @Override    public DataPage<Article> paged(int start, int size) {        Criteria criteria = getSession().createCriteria(Article.class);        criteria.add(Restrictions.eq(BaseInfoColumn.status.getName(), BaseInfo.Status.active));        return pageQuery(criteria, start, size);    }}