package com.tonyzhai.shop.portal.goods.persistence.impl;import com.tonyzhai.blog.domain.base.domain.BaseInfo;import com.tonyzhai.blog.domain.base.domain.support.DataPage;import com.tonyzhai.blog.domain.base.domain.support.column.BaseInfoColumn;import com.tonyzhai.blog.persistence.base.impl.BaseDaoImpl;import com.tonyzhai.shop.portal.goods.domain.GoodsCategory;import com.tonyzhai.shop.portal.goods.persistence.GoodsCategoryDao;import org.hibernate.Criteria;import org.hibernate.criterion.Restrictions;/** * Goods category persistence layer impl * * @author Tony Zhai * @since 13-5-3 PM7:58 */public class GoodsCategoryDaoImpl extends BaseDaoImpl<GoodsCategory> implements GoodsCategoryDao {    @Override    public void updateStatus(String id, BaseInfo.Status status) {        GoodsCategory goodsCategory=findById(id);        goodsCategory.getBaseInfo().setStatus(status);        update(goodsCategory);    }    @Override    public DataPage<GoodsCategory> paged(int start, int size) {        Criteria criteria=getSession().createCriteria(GoodsCategory.class);        criteria.add(Restrictions.eq(BaseInfoColumn.status.getName(), BaseInfo.Status.active));        return pageQuery(criteria,start,size);    }}