package com.tonyzhai.shop.security.extension;import com.tonyzhai.shop.domain.security.User;import org.springframework.security.core.GrantedAuthority;import java.util.Collection;/** * Custom User Details * * @author Tony Zhai * @since 13-3-3 AM12:24 */class UserDetails extends org.springframework.security.core.userdetails.User {    private User user;    public User getUser() {        return user;    }    public UserDetails(String username, String password, Collection<? extends GrantedAuthority> authorities) {        super(username, password, authorities);    }}