package com.tonyzhai.blog.service.impl;import com.tonyzhai.blog.domain.Article;import com.tonyzhai.blog.domain.base.domain.BaseInfo;import com.tonyzhai.blog.domain.base.domain.support.DataPage;import com.tonyzhai.blog.persistence.ArticleDao;import com.tonyzhai.blog.service.ArticleService;import com.tonyzhai.blog.util.BaseInfoUtil;import org.springframework.util.Assert;/** * Article service layer impl * * @author Tony Zhai * @since 13-2-24 PM5:33 */public class ArticleServiceImpl implements ArticleService {    private ArticleDao articleDao;    public void setArticleDao(ArticleDao articleDao) {        this.articleDao = articleDao;    }    @Override    public void createArticle(Article article) {        BaseInfoUtil.bindCreatedInformation(article.getBaseInfo());        articleDao.persist(article);    }    @Override    public void removeArticle(Article article) {        articleDao.updateStatus(article.getId(), BaseInfo.Status.delete);    }    @Override    public void removeArticle(String id) {        articleDao.updateStatus(id, BaseInfo.Status.delete);    }    @Override    public void modifyArticle(Article article) {        Article databaseArticle = articleDao.findById(article.getId());        databaseArticle.setTitle(article.getTitle());        databaseArticle.setContent(article.getContent());        BaseInfoUtil.bindUpdatedInformation(article.getBaseInfo());        articleDao.merge(article);    }    @Override    public Article findArticle(String id) {        return articleDao.findById(id);    }    @Override    public DataPage<Article> pagedArticles(int start, int size) {        Assert.isTrue(size > 0, "size must be not 0.");        return articleDao.paged(start, size);    }}