package com.tonyzhai.shop.service.impl;import com.tonyzhai.shop.domain.Article;import com.tonyzhai.shop.domain.base.BaseInfo;import com.tonyzhai.shop.domain.base.support.DataPage;import com.tonyzhai.shop.persistence.ArticleDao;import com.tonyzhai.shop.service.ArticleService;import com.tonyzhai.shop.util.BaseInfoUtil;import org.springframework.util.Assert;/** * Article service layer impl * * @author Tony Zhai * @since 13-2-24 PM5:33 */public class ArticleServiceImpl implements ArticleService {    private ArticleDao articleDao;    public void setArticleDao(ArticleDao articleDao) {        this.articleDao = articleDao;    }    @Override    public void createArticle(Article article) {        BaseInfoUtil.bindCreatedInformation(article.getBaseInfo());        articleDao.save(article);    }    @Override    public void removeArticle(Article article) {        articleDao.updateStatus(article.getId(), BaseInfo.Status.delete);    }    @Override    public void removeArticle(String id) {        articleDao.updateStatus(id, BaseInfo.Status.delete);    }    @Override    public void modifyArticle(Article article) {        BaseInfoUtil.bindUpdatedInformation(article.getBaseInfo());        articleDao.update(article);    }    @Override    public Article findArticle(String id) {        return articleDao.findById(id);    }    @Override    public DataPage<Article> pagedArticles(int start, int size) {        Assert.isTrue(size > 0, "size must be not 0.");        return articleDao.paged(start, size);    }}