package com.tonyzhai.shop.service.portal.goods.impl;import com.tonyzhai.shop.domain.base.BaseInfo;import com.tonyzhai.shop.domain.base.support.DataPage;import com.tonyzhai.shop.domain.portal.goods.GoodsCategory;import com.tonyzhai.shop.persistence.portal.goods.GoodsCategoryDao;import com.tonyzhai.shop.service.portal.goods.GoodsCategoryService;import org.springframework.util.Assert;/** * Goods Category service layer impl * * @author Tony Zhai * @since 13-5-3 PM8:01 */public class GoodsCategoryServiceImpl implements GoodsCategoryService {    private GoodsCategoryDao goodsCategoryDao;    public void setGoodsCategoryDao(GoodsCategoryDao goodsCategoryDao) {        this.goodsCategoryDao = goodsCategoryDao;    }    @Override    public void createGoodsCategory(GoodsCategory goodsCategory) {        goodsCategoryDao.save(goodsCategory);    }    @Override    public void removeGoodsCategory(GoodsCategory goodsCategory) {        goodsCategoryDao.updateStatus(goodsCategory.getId(), BaseInfo.Status.delete);    }    @Override    public void removeGoodsCategory(String id) {        goodsCategoryDao.updateStatus(id, BaseInfo.Status.delete);    }    @Override    public void modifyGoodsCategory(GoodsCategory goodsCategory) {        goodsCategoryDao.update(goodsCategory);    }    @Override    public GoodsCategory findGoodsCategory(String id) {        return goodsCategoryDao.findById(id);    }    @Override    public DataPage<GoodsCategory> pagedGoodsCategories(int start, int size) {        Assert.isTrue(size > 0, "size must be not 0.");        return goodsCategoryDao.paged(start, size);    }}