package com.tonyzhai.shop.web;import com.tonyzhai.shop.domain.Article;import com.tonyzhai.shop.domain.base.support.DataPage;import com.tonyzhai.shop.domain.base.support.exception.BaseException;import com.tonyzhai.shop.service.ArticleService;import com.tonyzhai.shop.web.base.AbstractController;import org.apache.log4j.Logger;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.validation.BindingResult;import org.springframework.web.bind.annotation.*;import javax.annotation.Resource;/** * Article controller * * @author Tony Zhai * @since 13-2-24 PM4:47 */@Controller@RequestMapping("/articles")public class ArticleController extends AbstractController {    private static final Logger logger = Logger.getLogger(ArticleController.class);    @Resource    private ArticleService articleService;    @RequestMapping(method = RequestMethod.POST, produces = "application/json")    @ResponseBody    public String create(@RequestBody Article article, BindingResult bindingResult, Model uiModel) {        if(bindingResult.hasErrors()) {            return ajaxFailed();        }        uiModel.asMap().clear();        articleService.createArticle(article);        return ajaxSuccess();    }    @RequestMapping(params = "form", produces = "text/html")    public String createForm() {        return "articles/create";    }    @RequestMapping(produces = "text/html")    public String list() {        return "articles/list";    }    @RequestMapping(value = "/json", produces = "application/json")    @ResponseBody    public DataPage<Article> loadJsonList(            @RequestParam(value = "page", required = false) int page,            @RequestParam(value = "size", required = false) int size) {        final int firstResult = (page - 1) * size;        DataPage<Article> dataPage = articleService.pagedArticles(firstResult, size);        return dataPage;    }    @RequestMapping(value = "/{id}", produces = "application/json")    @ResponseBody    public Article getArticle(@PathVariable("id") String id) {        return articleService.findArticle(id);    }    @RequestMapping(value = "/{id}",method = RequestMethod.PUT, produces = "application/json")    @ResponseBody    public String update(@RequestBody Article article, BindingResult bindingResult, Model uiModel) {        if(bindingResult.hasErrors()) {            return ajaxFailed();        }        uiModel.asMap().clear();        Article updateArticle = articleService.findArticle(article.getId());        updateArticle.setTitle(article.getTitle());        updateArticle.setContent(article.getContent());        articleService.modifyArticle(updateArticle);        return ajaxSuccess();    }    @RequestMapping(value = "/{id}", params = "form", produces = "text/html")    public String updateForm(@PathVariable("id") String id, Model uiModel) {        uiModel.addAttribute("id", id);        return "articles/update";    }    @RequestMapping(value = "/{id}", method = RequestMethod.DELETE, produces = "application/json")    @ResponseBody    public String delete(@PathVariable("id") String id) {        try {            articleService.removeArticle(id);            return ajaxSuccess();        } catch(BaseException e) {            logger.error(e.getMessage());            return ajaxFailed();        }    }}