package com.tonyzhai.blog.util;import com.tonyzhai.blog.domain.base.domain.BaseInfo;import com.tonyzhai.blog.domain.security.domain.User;import java.util.Date;/** * BaseInfo util * * @author Tony Zhai * @since 13-2-24 PM4:59 */public class BaseInfoUtil {    public static void bindCreatedInformation(BaseInfo baseInfo) {        User user = UserUtil.getCurrentUser();        if(user != null) {            baseInfo.setCreatedId(user.getId());            baseInfo.setCreatedFullName(user.getFullName());            baseInfo.setCreatedUserName(user.getUsername());            baseInfo.setModifiedId(user.getId());            baseInfo.setModifiedUserName(user.getUsername());            baseInfo.setModifiedFullName(user.getFullName());        }        baseInfo.setCreatedDate(new Date());        baseInfo.setModifiedDate(new Date());        baseInfo.setStatus(BaseInfo.Status.active);    }    public static void bindUpdatedInformation(BaseInfo baseInfo) {        User user = UserUtil.getCurrentUser();        if(user != null) {            baseInfo.setModifiedId(user.getId());            baseInfo.setModifiedUserName(user.getUsername());            baseInfo.setModifiedFullName(user.getFullName());        }        baseInfo.setModifiedDate(new Date());    }}