package com.tonyzhai.shop.util;import com.tonyzhai.shop.domain.base.BaseInfo;import com.tonyzhai.shop.domain.security.User;import com.tonyzhai.shop.security.extension.UserDetailsUtil;import java.util.Date;/** * BaseInfo util * * @author Tony Zhai * @since 13-2-24 PM4:59 */public class BaseInfoUtil {    public static void bindCreatedInformation(BaseInfo baseInfo) {        User user = UserDetailsUtil.getCurrentUser();        if(user != null) {            baseInfo.setCreatedId(user.getId());            baseInfo.setCreatedFullName(user.getFullName());            baseInfo.setCreatedUsername(user.getUsername());            baseInfo.setModifiedId(user.getId());            baseInfo.setModifiedUsername(user.getUsername());            baseInfo.setModifiedFullName(user.getFullName());        }        baseInfo.setCreatedDate(new Date());        baseInfo.setModifiedDate(new Date());        baseInfo.setStatus(BaseInfo.Status.active);    }    public static void bindUpdatedInformation(BaseInfo baseInfo) {        User user = UserDetailsUtil.getCurrentUser();        if(user != null) {            baseInfo.setModifiedId(user.getId());            baseInfo.setModifiedUsername(user.getUsername());            baseInfo.setModifiedFullName(user.getFullName());        }        baseInfo.setModifiedDate(new Date());    }}